var through = require( "through" );
var sass = require( "node-sass" );
var path = require( "path" );

module.exports = function( file ) {
	if( path.extname( file ) !== ".scss" )
		return through();

	var data = '';

	return through(write, end);

	function write(buf) {
		data += buf;
	}

	function end() {
		this.queue( sass.renderSync( data ) );
		this.queue( null );
	}
};